/***********************/
/*    color scheme     */
/***********************/
/***********************/
/*       global        */
/***********************/
* {
  box-sizing: border-box;
}

body {
  padding-top: 5em;
  font-family: "Sarala";
}

/***********************/
/* bootstrap overrides */
/***********************/
.container-fluid {
  width: 100%;
  max-width: 1400px;
}

.btn {
  border: 1px solid #adadad;
}

.navbar {
  border-radius: 0;
}

.jumbotron {
  background: #f7f7f7;
}

#work-details.hide {
  opacity: 0 !important;
}

#work-details.fade {
  opacity: 1 !important;
  margin-bottom: 5em;
  transition: 1s ease-in-out;
}

section#work-gallery button:focus {
  background: silver;
  outline: 0;
}

.project-cover-art > img {
  border-radius: 4px;
}
.project-cover-art h5 {
  font-size: 2rem;
}

.loader:before {
  content: "";
  position: absolute;
  top: 25%;
  left: 50%;
  width: 3rem;
  height: 3rem;
  margin-top: -30px;
  margin-left: -30px;
  border-radius: 50%;
  border: 0.25em solid black;
  border-right-color: transparent;
  animation: spin 0.75s linear infinite;
}

@keyframes spin {
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
}
/***********************/
/*       desktop       */
/***********************/
section#work-gallery {
  position: relative;
  padding: 5em 0em 5em 0em;
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-column-gap: 1rem;
}
section#work-gallery .overlay {
  position: absolute;
  top: 0;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 2em;
  height: 10px;
  width: 100%;
  background: rgba(0, 0, 0, 0.5);
  color: white;
  opacity: 0;
  text-align: center;
}
section#work-gallery .project-cover-art:hover .overlay {
  opacity: 1;
  transition: 0.5s ease-in;
  filter: blur(0px);
}

section#footer {
  background: #f7f7f7;
  margin-top: 0em;
}
section#footer footer {
  border-top: 1px solid #e7e7e7;
  padding: 0.3em 0em 1em 0em;
}
section#footer footer .footer__inner {
  padding: 3rem 0;
  display: flex;
  justify-content: space-evenly;
  align-items: baseline;
}
section#footer input {
  padding: 0.5em 0em;
  border-top-style: none;
  border-right-style: none;
  border-bottom-style: solid;
  border-left-style: none;
  outline: none;
  background: transparent;
  border-radius: 0;
}
section#footer textarea {
  resize: none;
  border: 2px solid black;
  border-top-style: none;
  border-right-style: none;
  border-bottom-style: solid;
  border-left-style: none;
  outline: none;
  padding: 0.5em 0em;
  background: transparent;
  border-radius: 0;
}
section#footer button[type=submit] {
  background: transparent;
  border: none;
  padding: 5px 0 5px 0;
  border-bottom: 3px solid red;
}
section#footer button[type=submit]:hover {
  font-weight: bold;
}
section#footer input[type=submit]:hover {
  font-weight: 900;
}
section#footer .footer-column p:first-child {
  padding: 2rem 0rem;
}
section#footer .footer-column a {
  color: black;
  text-decoration: underline;
}

section#navigation .navbar-header img {
  max-width: 150px;
}
section#navigation .navbar-social-media {
  padding: 0;
  font-size: 1.2em;
  font-family: Sarala;
  padding: 1em 0em 0em 1em;
}
section#navigation .navbar-social-media span {
  padding: 0 1em 0em 0em;
}
section#navigation a {
  color: black;
}
section#navigation .navbar-default {
  background: rgba(255, 255, 255, 0.9);
}
section#navigation .navbar-social-media {
  font-size: 1.3em;
  padding: 1em 0em;
}
section#navigation .navbar-social-media > a {
  font-weight: bold;
}
section#navigation .navbar-social-media i {
  padding: 0em 0.5em;
}
section#navigation .navbar-header {
  font-size: 1em;
  padding: 0.8em 0em 0em 1em;
}
section#navigation .navbar-header a {
  color: black;
}
section#navigation .navigation-style {
  width: 80%;
  margin: 0px auto;
}
section#navigation .navigation-style .navigation-style__inner {
  display: flex;
  justify-content: space-between;
  flex-direction: row;
  padding: 1rem 0;
}

section#banner button {
  margin: 0em 0.5em;
}

section#work-details {
  padding: 9em 0em 0em 0em;
}
section#work-details .project-description {
  display: block;
  padding: 0.2em 3em;
  font-size: 1.1em;
  font-weight: bolder;
}
section#work-details .project-header {
  display: block;
  padding: 0em 1.3em;
  font-size: 1.1em;
  font-weight: bolder;
}
section#work-details a {
  text-decoration: none;
  color: black;
}
section#work-details p {
  position: relative;
  font-size: 1.1em;
  padding: 1em 0em;
}
section#work-details .project-description:before {
  content: "";
  background: black;
  position: absolute;
  top: 29px;
  left: 0;
  width: 37px;
  height: 4px;
}
section#work-details .tag {
  display: inline-block;
  padding: 0.1em 1em;
  margin: 0.2em 0.2em;
  background: #e8e8e8;
  border-radius: 2px;
}

/***********************/
/*       mobile        */
/***********************/
@media only screen and (max-width: 993px) {
  section#footer {
    text-align: center;
  }

  section#navigation .navbar-header {
    padding: 0.5em 0em 0em 1em;
  }
  section#navigation .navbar-social-media {
    padding: 0;
    font-size: 1em;
    padding: 1.2em 0em 0em 1em;
    font-weight: bolder;
  }
  section#navigation .navbar-social-media span {
    padding: 0 0.3em;
  }
  section#navigation .navigation-style {
    width: 85%;
    margin: 0px auto;
  }

  section#work-details .space {
    margin: 2.5em 0;
  }

  section#work-gallery > div {
    grid-template-columns: repeat(2, 1fr);
  }
}
@media only screen and (max-width: 768px) {
  section#navigation .navigation-style {
    width: 100%;
    margin: 0px auto;
  }

  section#work-gallery {
    grid-template-columns: 1fr;
  }
}
@media only screen and (max-width: 500px) {
  .banner__text h1 {
    font-size: 2.5rem;
  }
  .banner__text h4 {
    font-size: 1.5rem;
  }

  .navigation-style .navigation-style__inner {
    flex-direction: column !important;
    align-items: center;
  }
}

